 118	func TestSuite_PostsAndTags(t *testing.T) {
 119		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 120		defer CleanupTestDB(t, db)
 121	
 122		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 123		postRepo := &repositories.GormRepository[TestPost]{DB: db}
 124		tagRepo := &repositories.GormRepository[TestTag]{DB: db}
 125		ctx := context.Background()
 126	
 127		t.Run("UserWithPostsAndTags", func(t *testing.T) {
 128			user := NewTestUserBuilder().
 129				WithName("Blogger User").
 130				WithEmail("blogger@example.com").
 131				Build()
 132	
 133			err := userRepo.Create(ctx, user)
 134			if err != nil {
 135				t.Fatalf("Failed to create user: %v", err)
 136			}
 137	
 138			// Create tags
 139			tag1 := TestTag{ID: uuid.New(), Name: "Go"}
 140			tag2 := TestTag{ID: uuid.New(), Name: "Testing"}
 141			tag3 := TestTag{ID: uuid.New(), Name: "GORM"}
 142	
 143			for _, tag := range []TestTag{tag1, tag2, tag3} {
 144				err = tagRepo.Create(ctx, tag)
 145				if err != nil {
 146					t.Fatalf("Failed to create tag %s: %v", tag.Name, err)
 147				}
 148			}
 149	
 150			// Create posts
 151			post1 := NewTestPostBuilder(user.ID).
 152				WithTitle("Introduction to Go").
 153				WithContent("Go is a great programming language...").
 154				WithPublished(true).
 155				Build()
 156	
 157			post2 := NewTestPostBuilder(user.ID).
 158				WithTitle("Testing with GORM").
 159				WithContent("GORM makes database testing easier...").
 160				WithPublished(false).
 161				Build()
 162	
 163			err = postRepo.Create(ctx, post1)
 164			if err != nil {
 165				t.Fatalf("Failed to create post1: %v", err)
 166			}
 167			err = postRepo.Create(ctx, post2)
 168			if err != nil {
 169				t.Fatalf("Failed to create post2: %v", err)
 170			}
 171	
 172			// Associate tags with posts
 173			err = postRepo.AppendAssociation(ctx, post1, "Tags", []TestTag{tag1, tag2})
 174			if err != nil {
 175				t.Fatalf("Failed to associate tags with post1: %v", err)
 176			}
 177	
 178			err = postRepo.AppendAssociation(ctx, post2, "Tags", []TestTag{tag2, tag3})
 179			if err != nil {
 180				t.Fatalf("Failed to associate tags with post2: %v", err)
 181			}
 182	
 183			// Find user with posts and their tags
 184			foundUser, err := userRepo.FindById(ctx, user.ID, repositories.WithRelations("Posts", "Posts.Tags"))
 185			if err != nil {
 186				t.Fatalf("Failed to find user with posts and tags: %v", err)
 187			}
 188	
 189			if len(foundUser.Posts) != 2 {
 190				t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 191			}
 192	
 193			// Check that posts have tags
 194			for _, post := range foundUser.Posts {
 195				if len(post.Tags) == 0 {
 196					t.Errorf("Expected post %s to have tags", post.Title)
 197				}
 198			}
 199	
 200			// Find published posts only
 201			publishedPosts, err := postRepo.FindMany(ctx,
 202				repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 203					return db.Where("user_id = ? AND published = ?", user.ID, true)
 204				}),
 205				repositories.WithRelations("Tags"),
 206			)
 207			if err != nil {
 208				t.Fatalf("Failed to find published posts: %v", err)
 209			}
 210	
 211			if len(publishedPosts) != 1 {
 212				t.Errorf("Expected 1 published post, got %d", len(publishedPosts))
 213			}
 214			if publishedPosts[0].Title != "Introduction to Go" {
 215				t.Errorf("Expected published post title 'Introduction to Go', got %s", publishedPosts[0].Title)
 216			}
 217		})
 218	}
