 16	const (
 17		txContextKey = "__tx"
 18	)
 19	
 20	type GormRepository[T any] struct {
 21		Repository[T]
 22		DB *gorm.DB
 23	}
 24	
 25	func WithRelations(relations ...string) Option {
 26		return func(db *gorm.DB) *gorm.DB {
 27			for _, relation := range relations {
 28				db = db.Preload(relation)
 29			}
 30			return db
 31		}
 32	}
 33	
 34	func applyOptions(db *gorm.DB, options []Option) *gorm.DB {
 35		for _, option := range options {
 36			if option != nil {
 37				db = option(db)
 38			}
 39		}
 40		return db
 41	}
 42	
 43	func newEntity[T any]() T {
 44		var entity T
 45		entityType := reflect.TypeOf(entity)
 46		if entityType.Kind() == reflect.Ptr {
 47			return reflect.New(entityType.Elem()).Interface().(T)
 48		}
 49		return entity
 50	}
 51	
 52	func (r *GormRepository[T]) FindMany(ctx context.Context, options ...Option) ([]T, error) {
 53		var entities []T
 54		db := applyOptions(r.DB, options).WithContext(ctx)
 55		if err := db.Find(&entities).Error; err != nil {
 56			return nil, err
 57		}
 58		return entities, nil
 59	}
 60	
 61	// FindPaginated retrieves records with pagination.
 62	func (r *GormRepository[T]) FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error) {
 63		var entities []T
 64		var totalRows int64
 65	
 66		db := applyOptions(r.DB, options).WithContext(ctx)
 67		db.Model(&entities).Count(&totalRows)
 68	
 69		offset := (page - 1) * pageSize
 70		if err := db.Offset(offset).Limit(pageSize).Find(&entities).Error; err != nil {
 71			return nil, err
 72		}
 73	
 74		result := &PaginationResult[T]{
 75			Data: entities,
 76			Total: totalRows,
 77			Limit: pageSize,
 78			Offset: offset,
 79			CurrentPage: page,
 80			LastPage: int((totalRows + int64(pageSize) - 1) / int64(pageSize)),
 81			From: offset + 1,
 82			To: offset + len(entities),
 83		}
 84	
 85		return result, nil
 86	}
 87	
 88	func (r *GormRepository[T]) FindOne(ctx context.Context, options ...Option) (T, error) {
 89		var entity T
 90		db := applyOptions(r.DB, options).WithContext(ctx)
 91	
 92		if err := db.First(&entity).Error; err != nil {
 93			return entity, err
 94		}
 95	
 96		// Store clone if in transaction and supports cloning
 97		storeCloneIfInTransaction(db, entity)
 98	
 99		return entity, nil
 100	}
 101	
 102	func (r *GormRepository[T]) FindById(ctx context.Context, id uuid.UUID, options ...Option) (T, error) {
 103		var entity T
 104		db := applyOptions(r.DB, options).WithContext(ctx)
 105		if err := db.First(&entity, "id = ?", id).Error; err != nil {
 106			return entity, err
 107		}
 108	
 109		// Store clone if in transaction and supports cloning
 110		storeCloneIfInTransaction(db, entity)
 111	
 112		return entity, nil
 113	}
 114	
 115	func (r *GormRepository[T]) Create(ctx context.Context, entity T, options ...Option) error {
 116		db := applyOptions(r.DB, options).WithContext(ctx)
 117		if err := db.Create(&entity).Error; err != nil {
 118			return err
 119		}
 120	
 121		storeCloneIfInTransaction(db, entity)
 122	
 123		return nil
 124	}
 125	
 126	func (r *GormRepository[T]) Save(ctx context.Context, entity T, options ...Option) error {
 127		db := applyOptions(r.DB, options).WithContext(ctx)
 128		return db.Save(&entity).Error
 129	}
...
 200	
 201	func (r *GormRepository[T]) UpdateByIdInPlace(ctx context.Context, id uuid.UUID, entity T, updateFunc func(T), options ...Option) error {
 202		db := applyOptions(r.DB, options).WithContext(ctx)
 203	
 204		// Check if entity supports cloning and diffing
 205		cloneable, isCloneable := any(entity).(Cloneable[T])
 206		if !isCloneable {
 207			return fmt.Errorf("entity does not support cloning - entity must implement Cloneable[T] interface")
 208		}
 209	
 210		_, isDiffable := any(entity).(Diffable[T])
 211		if !isDiffable {
 212			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 213		}
...
 237	
 238	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 239		db := applyOptions(r.DB, options).WithContext(ctx)
 240		return db.Delete(new(T), "id = ?", id).Error
 241	}
 242	
 243	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 244		return applyOptions(r.DB, options).
 245			WithContext(ctx).
 246			Model(&entity).
 247			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 248			Association(association).
 249			Append(values)
 250	}
...
 272	
 273	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 274	func (r *GormRepository[T]) BeginTransaction() *Tx {
 275		gtx := r.DB.Begin()
 276		return &Tx{
 277			gtx: gtx,
 278			committed: false,
 279			rolledBack: false,
 280			clonedEntities: make(map[string]interface{}),
 281		}
 282	}
 283	
 284	// WithTx returns an option to run the query within a transaction.
 285	// When used with Find operations, it automatically clones entities that support cloning.
 286	func WithTx(tx *Tx) Option {
 287		return func(db *gorm.DB) *gorm.DB {
 288			// Store the transaction reference in the context for later use
 289			return tx.gtx.Set(txContextKey, tx)
 290		}
 291	}
 292	
 293	// WithQuery returns an option to customize the query.
 294	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 295		return func(db *gorm.DB) *gorm.DB {
 296			return fn(db)
 297		}
 298	}
 299	
 300	func WithQueryStruct(query map[string]interface{}) Option {
 301		return func(db *gorm.DB) *gorm.DB {
 302			return db.Where(query)
 303		}
 304	}
 305	
 306	type Tx struct {
 307		gtx *gorm.DB
 308		committed bool
 309		rolledBack bool
 310		// clonedEntities stores cloned entities as snapshots during transaction
 311		// key is a unique identifier for the entity, value is the cloned entity snapshot
 312		clonedEntities map[string]interface{}
 313		mutex sync.RWMutex
 314	}
 315	
 316	// BeginTransaction starts a nested transaction
 317	func (tx *Tx) BeginTransaction() *Tx {
 318		gtx := tx.gtx.Begin()
 319		return &Tx{
 320			gtx: gtx,
 321			committed: false,
 322			rolledBack: false,
 323			clonedEntities: make(map[string]interface{}),
 324		}
 325	}
 326	
 327	// Commit commits the transaction
 328	func (tx *Tx) Commit() error {
 329		if tx.committed || tx.rolledBack {
 330			return nil
 331		}
 332	
 333		err := tx.gtx.Commit().Error
 334		if err == nil {
 335			tx.committed = true
 336		}
 337		return err
 338	}
 339	
 340	// Rollback rolls back the transaction
 341	func (tx *Tx) Rollback() error {
 342		if tx.committed || tx.rolledBack {
 343			return nil
 344		}
 345	
 346		err := tx.gtx.Rollback().Error
 347		if err == nil {
 348			tx.rolledBack = true
 349		}
 350		return err
 351	}
 352	
 353	// Finish should be called with defer to automatically handle commit/rollback
 354	// Usage: defer tx.Finish(&err)
 355	// Use this for simple cases where you don't need complex error handling
 356	// Will commit if err is nil, rollback if err is set
 357	func (tx *Tx) Finish(err *error) {
 358		if tx.committed || tx.rolledBack {
 359			return
 360		}
 361	
 362		if *err != nil {
 363			// If there was an error, rollback
 364			if rollbackErr := tx.Rollback(); rollbackErr != nil {
 365				// Log rollback error but don't override the original error
 366				// You might want to use your logging framework here
 367			}
 368		} else {
 369			// If no error, commit
 370			if commitErr := tx.Commit(); commitErr != nil {
 371				*err = commitErr
 372			}
 373		}
 374	}
 375	
 376	// Error returns any error from the underlying GORM transaction
 377	func (tx *Tx) Error() error {
 378		return tx.gtx.Error
 379	}
...
 417	
 418	// storeCloneIfInTransaction stores a clone of the entity if we're in a transaction and the entity supports cloning
 419	func storeCloneIfInTransaction[T any](db *gorm.DB, entity T) {
 420		// Check if we're in a transaction context
 421		txInterface, exists := db.Get(txContextKey)
 422		if !exists {
 423			return
 424		}
 425	
 426		tx, ok := txInterface.(*Tx)
 427		if !ok {
 428			return
 429		}
 430	
 431		// Check if entity supports cloning
 432		cloneable, ok := any(entity).(Cloneable[T])
 433		if !ok {
 434			return
 435		}
 436	
 437		// Store the cloned entity as a snapshot
 438		entityKey := generateEntityKey(entity)
 439		clone := cloneable.Clone()
 440		tx.storeClonedEntity(entityKey, clone)
 441	}
