 272	// AssertPaginationResult validates pagination result structure
 273	func AssertPaginationResult(t *testing.T, result *PaginationResult[tests.TestUser], expectedTotal int64, expectedPage int, expectedPageSize int, message string) {
 274		t.Helper()
 275		
 276		if result.Total != expectedTotal {
 277			t.Errorf("%s: Total mismatch - expected %d, got %d", message, expectedTotal, result.Total)
 278		}
 279		if result.CurrentPage != expectedPage {
 280			t.Errorf("%s: CurrentPage mismatch - expected %d, got %d", message, expectedPage, result.CurrentPage)
 281		}
 282		if result.Limit != expectedPageSize {
 283			t.Errorf("%s: Limit mismatch - expected %d, got %d", message, expectedPageSize, result.Limit)
 284		}
