 145	func TestGormRepository_FindPaginated(t *testing.T) {
 146		db := setupTestDB(t)
 147		repo := &GormRepository[tests.TestUser]{DB: db}
 148		ctx := context.Background()
 149	
 150		// Create 10 test users
 151		for i := 0; i < 10; i++ {
 152			user := tests.TestUser{
 153				ID: uuid.New(),
 154				Name: "User " + string(rune(i+'1')),
