 161				t.Fatalf("Failed to create test user: %v", err)
 162			}
 163		}
 164	
 165		// Test pagination
 166		result, err := repo.FindPaginated(ctx, 1, 5)
 167		if err != nil {
 168			t.Errorf("FindPaginated failed: %v", err)
 169		}
 170	
 171		if result.Total != 10 {
 172			t.Errorf("Expected total 10, got %d", result.Total)
 173		}
 174		if len(result.Data) != 5 {
 175			t.Errorf("Expected 5 items per page, got %d", len(result.Data))
 176		}
 177		if result.CurrentPage != 1 {
 178			t.Errorf("Expected current page 1, got %d", result.CurrentPage)
 179		}
 180		if result.LastPage != 2 {
 181			t.Errorf("Expected last page 2, got %d", result.LastPage)
 182		}
 183	}
