 221	func TestSuite_TransactionScenarios(t *testing.T) {
 222		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 223		defer CleanupTestDB(t, db)
 224	
 225		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 226		profileRepo := &repositories.GormRepository[TestProfile]{DB: db}
 227		ctx := context.Background()
 228	
 229		t.Run("ComplexTransactionSuccess", func(t *testing.T) {
 230			var err error
 231			tx := userRepo.BeginTransaction()
 232			defer tx.Finish(&err)
 233	
 234			// Create user in transaction
 235			user := NewTestUserBuilder().
 236				WithName("Transaction User").
 237				WithEmail("transaction@example.com").
 238				Build()
 239	
 240			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 241			if err != nil {
 242				t.Errorf("Failed to create user in transaction: %v", err)
 243				return
 244			}
 245	
 246			// Create profile in transaction
 247			profile := NewTestProfileBuilder(user.ID).
 248				WithBio("Transaction profile bio").
 249				Build()
 250	
 251			err = profileRepo.Create(ctx, profile, repositories.WithTx(tx))
 252			if err != nil {
 253				t.Errorf("Failed to create profile in transaction: %v", err)
 254				return
 255			}
 256	
 257			// Transaction should commit automatically
 258		})
 259	
 260		// Verify both user and profile were created
 261		users, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 262			return db.Where("email = ?", "transaction@example.com")
 263		}))
 264		if err != nil {
 265			t.Fatalf("Failed to find transaction user: %v", err)
 266		}
 267		if len(users) != 1 {
 268			t.Errorf("Expected 1 transaction user, got %d", len(users))
 269		}
 270	
 271		profiles, err := profileRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 272			return db.Where("user_id = ?", users[0].ID)
 273		}))
 274		if err != nil {
 275			t.Fatalf("Failed to find transaction profile: %v", err)
 276		}
 277		if len(profiles) != 1 {
 278			t.Errorf("Expected 1 transaction profile, got %d", len(profiles))
 279		}
 280	
 281		t.Run("ComplexTransactionFailure", func(t *testing.T) {
 282			var err error
 283			tx := userRepo.BeginTransaction()
 284			defer tx.Finish(&err)
 285	
 286			// Create user in transaction
 287			user := NewTestUserBuilder().
 288				WithName("Failed Transaction User").
 289				WithEmail("failed@example.com").
 290				Build()
 291	
 292			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 293			if err != nil {
 294				t.Errorf("Failed to create user in transaction: %v", err)
 295				return
 296			}
 297	
 298			// Simulate an error
 299			err = gorm.ErrInvalidTransaction
 300	
 301			// Transaction should rollback automatically
 302		})
 303	
 304		// Verify user was not created due to rollback
 305		failedUsers, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 306			return db.Where("email = ?", "failed@example.com")
 307		}))
 308		if err != nil {
 309			t.Fatalf("Failed to search for failed transaction user: %v", err)
 310		}
 311		if len(failedUsers) != 0 {
 312			t.Errorf("Expected 0 failed transaction users, got %d", len(failedUsers))
 313		}
 314	}
