 739			t.Errorf("FindPaginated with no data failed: %v", err)
 740		}
 741		if result.Total != 0 {
 742			t.Errorf("Expected total 0 with no data, got %d", result.Total)
 743		}
 744		if len(result.Data) != 0 {
 745			t.Errorf("Expected 0 items with no data, got %d", len(result.Data))
 746		}
 747		if result.LastPage != 0 {
 748			t.Errorf("Expected last page 0 with no data, got %d", result.LastPage)
 749		}
 750	
 751		// Create one user
 752		user := createTestUser()
 753		err = repo.Create(ctx, user)
 754		if err != nil {
 755			t.Fatalf("Failed to create test user: %v", err)
 756		}
 757	
 758		// Test pagination beyond available pages
 759		result, err = repo.FindPaginated(ctx, 5, 10)
 760		if err != nil {
 761			t.Errorf("FindPaginated beyond available pages failed: %v", err)
 762		}
 763		if result.Total != 1 {
 764			t.Errorf("Expected total 1, got %d", result.Total)
 765		}
 766		if len(result.Data) != 0 {
 767			t.Errorf("Expected 0 items on page beyond data, got %d", len(result.Data))
 768		}
