 786		if err != nil {
 787			t.Fatalf("Failed to create user: %v", err)
 788		}
 789	
 790		profileRepo := &GormRepository[tests.TestProfile]{DB: db}
 791		err = profileRepo.Create(ctx, profile)
 792		if err != nil {
 793			t.Fatalf("Failed to create profile: %v", err)
 794		}
 795	
 796		// Test chaining multiple options
 797		foundUser, err := repo.FindById(ctx, user.ID,
 798			WithRelations("Profile"),
 799			WithQuery(func(db *gorm.DB) *gorm.DB {
 800				return db.Where("active = ?", true)
 801			}),
 802		)
 803		if err != nil {
 804			t.Errorf("FindById with chained options failed: %v", err)
 805		}
 806	
 807		if foundUser.Profile == nil {
 808			t.Error("Expected profile to be loaded with chained options")
 809		}
 810		if foundUser.Active != true {
 811			t.Error("Expected user to match query condition")
 812		}
 813	}
