 832	func TestApplyOptions(t *testing.T) {
 833		db := setupTestDB(t)
 834	
 835		// Test with nil options
 836		result1 := applyOptions(db, nil)
 837		if result1 != db {
 838			t.Error("applyOptions with nil should return original db")
 839		}
 840	
 841		// Test with empty options
 842		result2 := applyOptions(db, []Option{})
 843		if result2 != db {
 844			t.Error("applyOptions with empty slice should return original db")
 845		}
 846	
 847		// Test with nil option in slice
 848		options := []Option{nil, WithQuery(func(db *gorm.DB) *gorm.DB {
 849			return db.Where("active = ?", true)
 850		})}
 851		result3 := applyOptions(db, options)
 852		if result3 == db {
 853			t.Error("applyOptions with valid option should return modified db")
 854		}
 855	}
