 178	func BenchmarkGormRepository_Transaction(b *testing.B) {
 179		db := setupBenchmarkDB(b)
 180		repo := &GormRepository[tests.TestUser]{DB: db}
 181		ctx := context.Background()
 182	
 183		b.ResetTimer()
 184		for i := 0; i < b.N; i++ {
 185			var err error
 186			tx := repo.BeginTransaction()
 187	
 188			user := tests.TestUser{
 189				ID: uuid.New(),
 190				Name: "Transaction User " + string(rune(i)),
 191				Email: "tx" + string(rune(i)) + "@example.com",
 192				Age: 25,
 193				Active: true,
 194			}
 195	
 196			err = repo.Create(ctx, user, WithTx(tx))
 197			if err != nil {
 198				tx.Rollback()
 199				b.Fatalf("Create in transaction failed: %v", err)
 200			}
 201	
 202			err = tx.Commit()
 203			if err != nil {
 204				b.Fatalf("Transaction commit failed: %v", err)
 205			}
 206		}
 207	}
