 126	func (r *GormRepository[T]) Save(ctx context.Context, entity T, options ...Option) error {
 127		db := applyOptions(r.DB, options).WithContext(ctx)
 128		return db.Save(&entity).Error
 129	}
 130	
 131	func (r *GormRepository[T]) UpdateByIdWithMap(ctx context.Context, id uuid.UUID, values map[string]interface{}, options ...Option) (T, error) {
 132		db := applyOptions(r.DB, options).WithContext(ctx)
 133		entity := newEntity[T]()
 134	
 135		if err := db.Model(&entity).Where("id = ?", id).Updates(values).Error; err != nil {
 136			return entity, err
 137		}
 138		return entity, nil
 139	}
 140	
 141	func (r *GormRepository[T]) UpdateByIdWithMask(ctx context.Context, id uuid.UUID, mask map[string]interface{}, entity T, options ...Option) error {
 142		db := applyOptions(r.DB, options).WithContext(ctx)
 143	
 144		updateMap, err := utils.EntityToMap(mask, entity)
 145		if err != nil {
 146			return err
 147		}
 148	
 149		return db.Model(&entity).Clauses(clause.Returning{}).Where("id = ?", id).Updates(updateMap).Error
 150	}
 151	
 152	func (r *GormRepository[T]) UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error {
 153		db := applyOptions(r.DB, options).WithContext(ctx)
 154	
 155		// Check if we're in a transaction context
 156		txInterface, exists := db.Get(txContextKey)
 157		if !exists {
 158			return fmt.Errorf("UpdateById requires a transaction context with a cloned entity - use WithTx option and ensure entity implements Cloneable[T] and Diffable[T] interfaces")
 159		}
 160	
 161		tx, ok := txInterface.(*Tx)
 162		if !ok {
 163			return fmt.Errorf("invalid transaction context")
 164		}
 165	
 166		// Check if the entity was cloned
 167		entityKey := generateEntityKey(entity)
 168		cloneInterface, found := tx.getClonedEntity(entityKey)
 169		if !found {
 170			return fmt.Errorf("entity was not cloned in this transaction - use FindById or FindOne with WithTx option to clone the entity first")
 171		}
 172	
 173		// Verify entity supports diffing
 174		if _, ok := any(entity).(Diffable[T]); !ok {
 175			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 176		}
 177	
 178		// Verify clone is the correct type
 179		clone, ok := cloneInterface.(T)
 180		if !ok {
 181			return fmt.Errorf("cloned entity type mismatch")
 182		}
 183	
 184		// Verify clone supports diffing
 185		cloneDiffable, ok := any(clone).(Diffable[T])
 186		if !ok {
 187			return fmt.Errorf("cloned entity does not support diffing")
 188		}
 189	
 190		// Generate diff between stored clone and current entity
 191		diff := cloneDiffable.Diff(entity)
 192		if len(diff) == 0 {
 193			// No changes, nothing to update
 194			return nil
 195		}
 196	
 197		// Perform the update using the diff
 198		return db.Model(&entity).Clauses(clause.Returning{}).Where("id = ?", id).Updates(diff).Error
 199	}
 200	
 201	func (r *GormRepository[T]) UpdateByIdInPlace(ctx context.Context, id uuid.UUID, entity T, updateFunc func(T), options ...Option) error {
 202		db := applyOptions(r.DB, options).WithContext(ctx)
 203	
 204		cloneable, isDiffable := any(entity).(Diffable[T])
 205		if !isDiffable {
 206			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 207		}
 208	
 209		// Clone the original entity to use for diff generation
 210		originalClone := cloneable.Clone()
 211	
 212		// Apply the update function to modify the entity in place
 213		updateFunc(entity)
 214	
 215		// Generate diff between original clone and updated entity
 216		// We need to use the original clone as the base for the diff
 217		originalDiffable, ok := any(originalClone).(Diffable[T])
 218		if !ok {
 219			return fmt.Errorf("cloned entity does not support diffing")
 220		}
 221		diff := originalDiffable.Diff(entity)
 222	
 223		if len(diff) == 0 {
 224			// No changes, nothing to update
 225			return nil
 226		}
 227	
 228		// Perform the update using the diff
 229		return db.Model(&entity).Clauses(clause.Returning{}).Where("id = ?", id).Updates(diff).Error
 230	}
 231	
 232	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 233		db := applyOptions(r.DB, options).WithContext(ctx)
 234		return db.Delete(new(T), "id = ?", id).Error
 235	}
 236	
 237	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 238		return applyOptions(r.DB, options).
 239			WithContext(ctx).
 240			Model(&entity).
 241			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 242			Association(association).
 243			Append(values)
 244	}
 245	
 246	func (r *GormRepository[T]) RemoveAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 247		return applyOptions(r.DB, options).
 248			WithContext(ctx).
 249			Model(&entity).
 250			Association(association).
 251			Delete(values)
 252	}
