 267	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 268	func (r *GormRepository[T]) BeginTransaction() *Tx {
 269		gtx := r.DB.Begin()
 270		return &Tx{
 271			gtx: gtx,
 272			committed: false,
 273			rolledBack: false,
 274			clonedEntities: make(map[string]interface{}),
 275		}
 276	}
