 278	// WithTx returns an option to run the query within a transaction.
 279	// When used with Find operations, it automatically clones entities that support cloning.
 280	func WithTx(tx *Tx) Option {
 281		return func(db *gorm.DB) *gorm.DB {
 282			// Store the transaction reference in the context for later use
 283			return tx.gtx.Set(txContextKey, tx)
 284		}
 285	}
