 41			diff["name"] = u.Name
 42		}
 43		if u.Email != other.Email {
 44			diff["email"] = u.Email
 45		}
 46		if u.Age != other.Age {
 47			diff["age"] = u.Age
 48		}
 49		if u.Active != other.Active {
 50			diff["active"] = u.Active
 51		}
 52	
