 31	// EntityToMap converts an entity into a map based on specified fields.
 32	func EntityToMap(fields map[string]interface{}, entity interface{}) (map[string]interface{}, error) {
 33		entityValue := reflect.Indirect(reflect.ValueOf(entity))
 34		entityType := entityValue.Type()
 35	
 36		// Get or create the field info mapping
 37		fieldInfoMap := getFieldInfoMap(entityType)
 38	
 39		// Create the update map with exact capacity
 40		updateMap := make(map[string]interface{}, len(fields))
...
 132	
 133		// Not in cache, create mapping
 134		fieldMap = make(map[string]fieldInfo, entityType.NumField())
 135		namingStrategy := CamelCaseNamingStrategy{}
 136	
 137		// Add each field to the mapping
 138		for i := 0; i < entityType.NumField(); i++ {
 139			field := entityType.Field(i)
 140	
 141			// Skip unexported fields
 142			if field.PkgPath != "" {
 143				continue
 144			}
 145	
 146			// Calculate column name directly
 147			columnName := namingStrategy.ColumnName("", field.Name)
 148	
 149			fieldMap[field.Name] = fieldInfo{
 150				ColumnName: columnName,
 151				Index: i,
 152				IsPtr: field.Type.Kind() == reflect.Ptr,
 153			}
 154		}
 155	
 156		// Store in cache with write lock
 157		typeCacheMutex.Lock()
 158		// Double check if another goroutine already created the mapping
 159		if existing, found := fieldInfoCache[entityType]; found {
 160			typeCacheMutex.Unlock()
 161			return existing
 162		}
...
Path: utils/camel_case_naming_strategy.go
 1	package utils
 2	
 3	import (
 4		"strings"
 5		"unicode"
 6	
 7		"gorm.io/gorm/schema"
 8	)
 9	
 10	// CamelCaseNamingStrategy implements the schema.Namer interface to use camelCase
 11	type CamelCaseNamingStrategy struct{}
 12	
 13	// TableName converts table names to camelCase
 14	func (CamelCaseNamingStrategy) TableName(table string) string {
 15		return table
 16	}
 17	
 18	// SchemaName returns the schema name
 19	func (CamelCaseNamingStrategy) SchemaName(schema string) string {
 20		return schema
 21	}
 22	
 23	// ColumnName converts column names to camelCase with the first character lower case
 24	func (CamelCaseNamingStrategy) ColumnName(table, column string) string {
 25		if column == "" {
 26			return column
 27		}
 28		return toLowerCamelCase(column)
 29	}
...
 50	
 51	// UniqueName returns the unique constraint name
 52	func (CamelCaseNamingStrategy) UniqueName(table, column string) string {
 53		return "uq_" + table + "_" + column
 54	}
 55	
 56	// toLowerCamelCase converts a string to camelCase with the first character in lower case
 57	func toLowerCamelCase(s string) string {
 58		runes := []rune(s)
 59		for i, r := range runes {
 60			if i == 0 {
 61				runes[i] = unicode.ToLower(r)
 62			} else if unicode.IsUpper(r) {
 63				runes[i] = unicode.ToLower(r)
 64			} else {
 65				break
 66			}
 67		}
 68		return string(runes)
 69	}
