 267	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 268	func (r *GormRepository[T]) BeginTransaction() *Tx {
 269		gtx := r.DB.Begin()
 270		return &Tx{
 271			gtx: gtx,
 272			committed: false,
 273			rolledBack: false,
 274			clonedEntities: make(map[string]interface{}),
 275		}
 276	}
 277	
 278	// WithTx returns an option to run the query within a transaction.
 279	// When used with Find operations, it automatically clones entities that support cloning.
 280	func WithTx(tx *Tx) Option {
 281		return func(db *gorm.DB) *gorm.DB {
 282			// Store the transaction reference in the context for later use
 283			return tx.gtx.Set(txContextKey, tx)
 284		}
 285	}
 286	
 287	// WithQuery returns an option to customize the query.
 288	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 289		return func(db *gorm.DB) *gorm.DB {
 290			return fn(db)
 291		}
 292	}
 293	
 294	func WithQueryStruct(query map[string]interface{}) Option {
 295		return func(db *gorm.DB) *gorm.DB {
 296			return db.Where(query)
 297		}
 298	}
 299	
 300	type Tx struct {
 301		gtx *gorm.DB
 302		committed bool
 303		rolledBack bool
 304		// clonedEntities stores cloned entities as snapshots during transaction
 305		// key is a unique identifier for the entity, value is the cloned entity snapshot
 306		clonedEntities map[string]interface{}
 307		mutex sync.RWMutex
 308	}
 309	
 310	// BeginTransaction starts a nested transaction
 311	func (tx *Tx) BeginTransaction() *Tx {
 312		gtx := tx.gtx.Begin()
 313		return &Tx{
 314			gtx: gtx,
 315			committed: false,
 316			rolledBack: false,
 317			clonedEntities: make(map[string]interface{}),
 318		}
 319	}
 320	
 321	// Commit commits the transaction
 322	func (tx *Tx) Commit() error {
 323		if tx.committed || tx.rolledBack {
 324			return nil
 325		}
 326	
 327		err := tx.gtx.Commit().Error
 328		if err == nil {
 329			tx.committed = true
 330		}
 331		return err
 332	}
 333	
 334	// Rollback rolls back the transaction
 335	func (tx *Tx) Rollback() error {
 336		if tx.committed || tx.rolledBack {
 337			return nil
 338		}
 339	
 340		err := tx.gtx.Rollback().Error
 341		if err == nil {
 342			tx.rolledBack = true
 343		}
 344		return err
 345	}
 346	
 347	// Finish should be called with defer to automatically handle commit/rollback
 348	// Usage: defer tx.Finish(&err)
 349	// Use this for simple cases where you don't need complex error handling
 350	// Will commit if err is nil, rollback if err is set
 351	func (tx *Tx) Finish(err *error) {
 352		if tx.committed || tx.rolledBack {
 353			return
 354		}
 355	
 356		if *err != nil {
 357			// If there was an error, rollback
 358			if rollbackErr := tx.Rollback(); rollbackErr != nil {
 359				// Log rollback error but don't override the original error
 360				// You might want to use your logging framework here
 361			}
 362		} else {
 363			// If no error, commit
 364			if commitErr := tx.Commit(); commitErr != nil {
 365				*err = commitErr
 366			}
 367		}
 368	}
 369	
 370	// Error returns any error from the underlying GORM transaction
 371	func (tx *Tx) Error() error {
 372		return tx.gtx.Error
 373	}
 374	
 375	// storeClonedEntity stores the original entity before cloning
 376	func (tx *Tx) storeClonedEntity(entityKey string, original interface{}) {
 377		tx.mutex.Lock()
 378		defer tx.mutex.Unlock()
 379		tx.clonedEntities[entityKey] = original
 380	}
 381	
 382	// getClonedEntity retrieves the original entity if it was cloned
 383	func (tx *Tx) getClonedEntity(entityKey string) (interface{}, bool) {
 384		tx.mutex.RLock()
 385		defer tx.mutex.RUnlock()
 386		original, exists := tx.clonedEntities[entityKey]
 387		return original, exists
 388	}
 389	
 390	// generateEntityKey creates a unique key for an entity based on its type and ID
 391	func generateEntityKey(entity interface{}) string {
 392		entityType := reflect.TypeOf(entity)
 393		if entityType.Kind() == reflect.Ptr {
 394			entityType = entityType.Elem()
 395		}
 396	
 397		// Try to get ID field using reflection
 398		entityValue := reflect.ValueOf(entity)
 399		if entityValue.Kind() == reflect.Ptr {
 400			entityValue = entityValue.Elem()
 401		}
 402	
 403		idField := entityValue.FieldByName("Id")
 404		if !idField.IsValid() {
 405			// Fallback to memory address if no ID field
 406			return fmt.Sprintf("%s_%p", entityType.Name(), entity)
 407		}
 408	
 409		return fmt.Sprintf("%s_%v", entityType.Name(), idField.Interface())
 410	}
 411	
 412	// storeCloneIfInTransaction stores a clone of the entity if we're in a transaction and the entity supports cloning
 413	func storeCloneIfInTransaction[T any](db *gorm.DB, entity T) {
 414		// Check if we're in a transaction context
 415		txInterface, exists := db.Get(txContextKey)
 416		if !exists {
 417			return
 418		}
 419	
 420		tx, ok := txInterface.(*Tx)
 421		if !ok {
 422			return
 423		}
 424	
 425		// Check if entity supports cloning
 426		cloneable, ok := any(entity).(Diffable[T])
 427		if !ok {
 428			return
 429		}
 430	
 431		// Store the cloned entity as a snapshot
 432		entityKey := generateEntityKey(entity)
 433		clone := cloneable.Clone()
 434		tx.storeClonedEntity(entityKey, clone)
 435	}
