 240	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 241		db := applyOptions(r.DB, options).WithContext(ctx)
 242		return db.Delete(new(T), "id = ?", id).Error
