 1	package utils
 2	
 3	import (
 4		"encoding/json"
 5		"errors"
 6		"reflect"
 7		"strings"
 8		"sync"
 9	
 10		"gorm.io/gorm"
 11		"gorm.io/gorm/clause"
 12	)
 13	
 14	// Enhanced field info with index for faster access
 15	type fieldInfo struct {
 16		ColumnName string
 17		Index int
 18		IsPtr bool
 19	}
 20	
 21	// Enhanced cache structures
 22	var (
 23		typeCacheMutex sync.RWMutex
 24		fieldInfoCache = make(map[reflect.Type]map[string]fieldInfo)
 25		jsonNameCache sync.Map
 26	)
 27	
 28	// Small field count optimization
 29	const smallFieldCount = 4
 30	
 31	// EntityToMap converts an entity into a map based on specified fields.
 32	func EntityToMap(fields map[string]interface{}, entity interface{}) (map[string]interface{}, error) {
 33		entityValue := reflect.Indirect(reflect.ValueOf(entity))
 34		entityType := entityValue.Type()
 35	
 36		// Get or create the field info mapping
 37		fieldInfoMap := getFieldInfoMap(entityType)
 38	
 39		// Create the update map with exact capacity
 40		updateMap := make(map[string]interface{}, len(fields))
 41	
 42		// Fast path for small field counts (common case)
 43		if len(fields) <= smallFieldCount {
 44			// Process each field
 45			for key, value := range fields {
 46				info, found := fieldInfoMap[key]
 47				if !found {
 48					return nil, errors.New("field not found in entity: " + key)
 49				}
 50	
 51				// Get field by index instead of by name (much faster)
 52				fieldValue := entityValue.Field(info.Index)
 53	
 54				// Handle pointer types
 55				if info.IsPtr {
 56					if fieldValue.IsNil() {
 57						return nil, errors.New("nil pointer encountered for field: " + key)
 58					}
 59					fieldValue = fieldValue.Elem()
 60				}
 61	
 62				// Handle nested fields
 63				if subMap, ok := value.(map[string]interface{}); ok {
 64					subUpdateMap, err := handleNestedFields(fieldValue, subMap)
 65					if err != nil {
 66						return nil, err
 67					}
 68	
 69					jsonValue, err := json.Marshal(subUpdateMap)
 70					if err != nil {
 71						return nil, err
 72					}
 73	
 74					updateMap[info.ColumnName] = gorm.Expr("? || ?", clause.Column{Name: info.ColumnName}, string(jsonValue))
 75				} else {
 76					updateMap[info.ColumnName] = fieldValue.Interface()
 77				}
 78			}
 79			return updateMap, nil
 80		}
 81	
 82		// Regular path for larger field counts
 83		for key, value := range fields {
 84			info, found := fieldInfoMap[key]
 85			if !found {
 86				return nil, errors.New("field not found in entity: " + key)
 87			}
 88	
 89			// Get field by index instead of by name
 90			fieldValue := entityValue.Field(info.Index)
 91	
 92			if info.IsPtr {
 93				if fieldValue.IsNil() {
 94					return nil, errors.New("nil pointer encountered for field: " + key)
 95				}
 96				fieldValue = fieldValue.Elem()
 97			}
 98	
 99			if subMap, ok := value.(map[string]interface{}); ok {
 100				subUpdateMap, err := handleNestedFields(fieldValue, subMap)
 101				if err != nil {
 102					return nil, err
 103				}
 104	
 105				jsonValue, err := json.Marshal(subUpdateMap)
 106				if err != nil {
 107					return nil, err
 108				}
 109	
 110				updateMap[info.ColumnName] = gorm.Expr("? || ?", clause.Column{Name: info.ColumnName}, string(jsonValue))
 111			} else {
 112				updateMap[info.ColumnName] = fieldValue.Interface()
 113			}
 114		}
 115	
 116		return updateMap, nil
 117	}
 118	
 119	// Cache for column names
 120	var columnNameCache sync.Map
 121	
 122	// getFieldInfoMap retrieves or creates detailed field info mapping for a type
 123	func getFieldInfoMap(entityType reflect.Type) map[string]fieldInfo {
 124		// Check cache first using read lock (faster)
 125		typeCacheMutex.RLock()
 126		fieldMap, found := fieldInfoCache[entityType]
 127		typeCacheMutex.RUnlock()
 128	
 129		if found {
 130			return fieldMap
 131		}
 132	
 133		// Not in cache, create mapping
 134		fieldMap = make(map[string]fieldInfo, entityType.NumField())
 135		namingStrategy := CamelCaseNamingStrategy{}
 136	
 137		// Add each field to the mapping
 138		for i := 0; i < entityType.NumField(); i++ {
 139			field := entityType.Field(i)
 140	
 141			// Skip unexported fields
 142			if field.PkgPath != "" {
 143				continue
 144			}
 145	
 146			// Calculate column name directly
 147			columnName := namingStrategy.ColumnName("", field.Name)
 148	
 149			fieldMap[field.Name] = fieldInfo{
 150				ColumnName: columnName,
 151				Index: i,
 152				IsPtr: field.Type.Kind() == reflect.Ptr,
 153			}
 154		}
 155	
 156		// Store in cache with write lock
 157		typeCacheMutex.Lock()
 158		// Double check if another goroutine already created the mapping
 159		if existing, found := fieldInfoCache[entityType]; found {
 160			typeCacheMutex.Unlock()
 161			return existing
 162		}
 163		fieldInfoCache[entityType] = fieldMap
 164		typeCacheMutex.Unlock()
 165	
 166		return fieldMap
 167	}
 168	
 169	// Enhanced field info cache for nested fields
 170	type nestedFieldInfo struct {
 171		Index int
 172		JSONName string
 173		IsPtr bool
 174	}
 175	
 176	// Cache for nested field access
 177	var nestedFieldCache sync.Map // map[reflect.Type]map[string]nestedFieldInfo
 178	
 179	// handleNestedFields processes nested field structures
 180	func handleNestedFields(fieldValue reflect.Value, subMap map[string]interface{}) (map[string]interface{}, error) {
 181		subUpdateMap := make(map[string]interface{}, len(subMap))
 182	
 183		switch fieldValue.Kind() {
 184		case reflect.Struct:
 185			fieldType := fieldValue.Type()
 186	
 187			// Get or create nested field info
 188			var nestedFields map[string]nestedFieldInfo
 189	
 190			// Check cache first
 191			if cached, found := nestedFieldCache.Load(fieldType); found {
 192				nestedFields = cached.(map[string]nestedFieldInfo)
 193			} else {
 194				// Create new mapping
 195				nestedFields = make(map[string]nestedFieldInfo, fieldType.NumField())
 196	
 197				for i := 0; i < fieldType.NumField(); i++ {
 198					field := fieldType.Field(i)
 199	
 200					// Skip unexported
 201					if field.PkgPath != "" {
 202						continue
 203					}
 204	
 205					jsonName := getJSONName(field)
 206					nestedFields[field.Name] = nestedFieldInfo{
 207						Index: i,
 208						JSONName: jsonName,
 209						IsPtr: field.Type.Kind() == reflect.Ptr,
 210					}
 211				}
 212	
 213				// Store in cache
 214				nestedFieldCache.Store(fieldType, nestedFields)
 215			}
 216	
 217			for subKey, subValue := range subMap {
 218				info, found := nestedFields[subKey]
 219				if !found {
 220					return nil, errors.New("field not found: " + subKey)
 221				}
 222	
 223				// Access field by index (faster than FieldByName)
 224				subFieldValue := fieldValue.Field(info.Index)
 225	
 226				// Handle pointer types
 227				if info.IsPtr {
 228					if subFieldValue.IsNil() {
 229						return nil, errors.New("nil pointer for field: " + subKey)
 230					}
 231					subFieldValue = subFieldValue.Elem()
 232				}
 233	
 234				// Handle nested maps recursively
 235				if nestedMap, ok := subValue.(map[string]interface{}); ok {
 236					nestedResult, err := handleNestedFields(subFieldValue, nestedMap)
 237					if err != nil {
 238						return nil, err
 239					}
 240					subUpdateMap[info.JSONName] = nestedResult
 241				} else {
 242					subUpdateMap[info.JSONName] = subFieldValue.Interface()
 243				}
 244			}
 245	
 246		case reflect.Map:
 247			for subKey, subValue := range subMap {
 248				keyValue := reflect.ValueOf(subKey)
 249				mapValue := fieldValue.MapIndex(keyValue)
 250	
 251				if mapValue.IsValid() {
 252					if nestedMap, ok := subValue.(map[string]interface{}); ok && mapValue.IsValid() {
 253						nestedResult, err := handleNestedFields(mapValue, nestedMap)
 254						if err != nil {
 255							return nil, err
 256						}
 257						subUpdateMap[subKey] = nestedResult
 258					} else {
 259						subUpdateMap[subKey] = mapValue.Interface()
 260					}
 261				} else {
 262					subUpdateMap[subKey] = nil
 263				}
 264			}
 265	
 266		default:
 267			return nil, errors.New("unsupported type for nested fields")
 268		}
 269	
 270		return subUpdateMap, nil
 271	}
 272	
 273	// getJSONName extracts the JSON field name from struct tags with caching
 274	func getJSONName(field reflect.StructField) string {
 275		// Use unique key based on package path, struct and field name
 276		cacheKey := field.PkgPath + "." + field.Name
 277	
 278		// Check cache first
 279		if cachedName, ok := jsonNameCache.Load(cacheKey); ok {
 280			return cachedName.(string)
 281		}
 282	
 283		// Calculate JSON name
 284		tag := field.Tag.Get("json")
 285		var result string
 286	
 287		if tag == "" || tag == "-" {
 288			result = field.Name
 289		} else {
 290			// Get the part before the first comma
 291			if idx := strings.IndexByte(tag, ','); idx != -1 {
 292				tag = tag[:idx]
 293			}
 294	
 295			if tag == "" {
 296				result = field.Name
 297			} else {
 298				result = tag
 299			}
 300		}
 301	
 302		// Store in cache
 303		jsonNameCache.Store(cacheKey, result)
 304		return result
 305	}
