 9	func TestCamelCaseNamingStrategy_TableName(t *testing.T) {
 10		strategy := CamelCaseNamingStrategy{}
 11	
 12		tests := []struct {
 13			input string
 14			expected string
 15		}{
 16			{"users", "users"},
 17			{"user_profiles", "user_profiles"},
 18			{"UserProfiles", "UserProfiles"},
 19			{"", ""},
 20		}
 21	
 22		for _, test := range tests {
 23			result := strategy.TableName(test.input)
 24			if result != test.expected {
 25				t.Errorf("TableName(%s) = %s, expected %s", test.input, result, test.expected)
 26			}
 27		}
 28	}
