 138	func TestCamelCaseNamingStrategy_IndexName(t *testing.T) {
 139		strategy := CamelCaseNamingStrategy{}
 140	
 141		tests := []struct {
 142			table string
 143			column string
 144			expected string
 145		}{
 146			{"users", "email", "idx_users_email"},
