 159	
 160	func (r *GormRepository[T]) UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error {
 161		db := applyOptions(r.DB, options).WithContext(ctx)
 162	
 163		// Check if we're in a transaction context
 164		txInterface, exists := db.Get(txContextKey)
 165		if !exists {
 166			return fmt.Errorf("UpdateById requires a transaction context with a cloned entity - use WithTx option and ensure entity implements Cloneable[T] and Diffable[T] interfaces")
 167		}
 168	
 169		tx, ok := txInterface.(*Tx)
 170		if !ok {
 171			return fmt.Errorf("invalid transaction context")
 172		}
 173	
 174		// Check if the entity was cloned
 175		entityKey := generateEntityKey(entity)
 176		cloneInterface, found := tx.getClonedEntity(entityKey)
 177		if !found {
 178			return fmt.Errorf("entity was not cloned in this transaction - use FindById or FindOne with WithTx option to clone the entity first")
 179		}
 180	
 181		// Verify entity supports diffing
 182		if _, ok := any(entity).(Diffable[T]); !ok {
 183			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 184		}
 185	
 186		// Verify clone is the correct type
 187		clone, ok := cloneInterface.(T)
 188		if !ok {
 189			return fmt.Errorf("cloned entity type mismatch")
 190		}
 191	
 192		// Verify clone supports diffing
 193		cloneDiffable, ok := any(clone).(Diffable[T])
 194		if !ok {
 195			return fmt.Errorf("cloned entity does not support diffing")
 196		}
 197	
 198		// Generate diff between stored clone and current entity
 199		diff := cloneDiffable.Diff(entity)
 200		if len(diff) == 0 {
 201			// No changes, nothing to update
 202			return nil
 203		}
 204	
 205		// Perform the update using the diff
 206		return db.Model(&entity).Clauses(clause.Returning{}).Where("id = ?", id).Updates(diff).Error
 207	}
 208	
