 210		db := applyOptions(r.DB, options).WithContext(ctx)
 211	
 212		cloneable, isDiffable := any(entity).(Diffable[T])
 213		if !isDiffable {
 214			return fmt.Errorf("entity does not support diffing - entity must implement Diffable[T] interface")
 215		}
