 22	// Clone implements the Cloneable interface
 23	func (u TestUser) Clone() TestUser {
 24		clone := u
 25		if u.Profile != nil {
 26			profileClone := *u.Profile
 27			clone.Profile = &profileClone
 28		}
 29		if u.Posts != nil {
 30			clone.Posts = make([]TestPost, len(u.Posts))
 31			copy(clone.Posts, u.Posts)
 32		}
 33		return clone
 34	}
