 10	// CamelCaseNamingStrategy implements the schema.Namer interface to use camelCase
 11	type CamelCaseNamingStrategy struct{}
 12	
 13	// TableName converts table names to camelCase
 14	func (CamelCaseNamingStrategy) TableName(table string) string {
 15		return table
 16	}
 17	
 18	// SchemaName returns the schema name
 19	func (CamelCaseNamingStrategy) SchemaName(schema string) string {
 20		return schema
 21	}
 22	
 23	// ColumnName converts column names to camelCase with the first character lower case
 24	func (CamelCaseNamingStrategy) ColumnName(table, column string) string {
 25		if column == "" {
 26			return column
 27		}
 28		return toLowerCamelCase(column)
 29	}
 30	
 31	// JoinTableName returns the join table name
 32	func (CamelCaseNamingStrategy) JoinTableName(joinTable string) string {
 33		return joinTable
 34	}
 35	
 36	// RelationshipFKName returns the foreign key name
 37	func (CamelCaseNamingStrategy) RelationshipFKName(rel schema.Relationship) string {
 38		return strings.ToLower(rel.Name) + "_" + strings.ToLower(rel.Field.Name) + "_fkey"
 39	}
 40	
 41	// CheckerName returns the checker name
 42	func (CamelCaseNamingStrategy) CheckerName(table, column string) string {
 43		return "chk_" + table + "_" + column
 44	}
 45	
 46	// IndexName returns the index name
 47	func (CamelCaseNamingStrategy) IndexName(table, column string) string {
 48		return "idx_" + table + "_" + column
 49	}
 50	
 51	// UniqueName returns the unique constraint name
 52	func (CamelCaseNamingStrategy) UniqueName(table, column string) string {
 53		return "uq_" + table + "_" + column
 54	}
 55	
 56	// toLowerCamelCase converts a string to camelCase with the first character in lower case
 57	func toLowerCamelCase(s string) string {
 58		runes := []rune(s)
 59		for i, r := range runes {
 60			if i == 0 {
 61				runes[i] = unicode.ToLower(r)
 62			} else if unicode.IsUpper(r) {
 63				runes[i] = unicode.ToLower(r)
 64			} else {
 65				break
 66			}
 67		}
 68		return string(runes)
 69	}
