 16	const (
 17		txContextKey = "__tx"
 18	)
 19	
 20	type GormRepository[T any] struct {
 21		Repository[T]
 22		DB *gorm.DB
 23	}
 24	
 25	// NewGormRepository creates a new instance of GormRepository with the provided GORM database connection.
 26	// T is the entity type that this repository will manage.
 27	func NewGormRepository[T any](db *gorm.DB) *GormRepository[T] {
 28		return &GormRepository[T]{
 29			DB: db,
 30		}
 31	}
 32	
 33	func WithRelations(relations ...string) Option {
 34		return func(db *gorm.DB) *gorm.DB {
 35			for _, relation := range relations {
 36				db = db.Preload(relation)
 37			}
 38			return db
 39		}
 40	}
 41	
 42	func applyOptions(db *gorm.DB, options []Option) *gorm.DB {
 43		for _, option := range options {
 44			if option != nil {
 45				db = option(db)
 46			}
 47		}
 48		return db
 49	}
 50	
 51	func newEntity[T any]() T {
 52		var entity T
 53		entityType := reflect.TypeOf(entity)
 54		if entityType.Kind() == reflect.Ptr {
 55			return reflect.New(entityType.Elem()).Interface().(T)
 56		}
 57		return entity
 58	}
 59	
 60	func (r *GormRepository[T]) FindMany(ctx context.Context, options ...Option) ([]T, error) {
 61		var entities []T
 62		db := applyOptions(r.DB, options).WithContext(ctx)
 63		if err := db.Find(&entities).Error; err != nil {
 64			return nil, err
 65		}
 66		return entities, nil
 67	}
 68	
 69	// FindPaginated retrieves records with pagination.
 70	func (r *GormRepository[T]) FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error) {
 71		var entities []T
 72		var totalRows int64
 73	
 74		db := applyOptions(r.DB, options).WithContext(ctx)
 75		db.Model(&entities).Count(&totalRows)
 76	
 77		offset := (page - 1) * pageSize
 78		if err := db.Offset(offset).Limit(pageSize).Find(&entities).Error; err != nil {
 79			return nil, err
 80		}
 81	
 82		result := &PaginationResult[T]{
 83			Data: entities,
 84			Total: totalRows,
 85			Limit: pageSize,
 86			Offset: offset,
 87			CurrentPage: page,
 88			LastPage: int((totalRows + int64(pageSize) - 1) / int64(pageSize)),
 89			From: offset + 1,
 90			To: offset + len(entities),
 91		}
 92	
 93		return result, nil
 94	}
 95	
 96	func (r *GormRepository[T]) FindOne(ctx context.Context, options ...Option) (T, error) {
 97		var entity T
 98		db := applyOptions(r.DB, options).WithContext(ctx)
 99	
 100		if err := db.First(&entity).Error; err != nil {
 101			return entity, err
 102		}
 103	
 104		// Store clone if in transaction and supports cloning
 105		storeCloneIfInTransaction(db, entity)
 106	
 107		return entity, nil
 108	}
 109	
 110	func (r *GormRepository[T]) FindById(ctx context.Context, id uuid.UUID, options ...Option) (T, error) {
 111		var entity T
 112		db := applyOptions(r.DB, options).WithContext(ctx)
 113		if err := db.First(&entity, "id = ?", id).Error; err != nil {
 114			return entity, err
 115		}
 116	
 117		// Store clone if in transaction and supports cloning
 118		storeCloneIfInTransaction(db, entity)
 119	
 120		return entity, nil
 121	}
 122	
 123	func (r *GormRepository[T]) Create(ctx context.Context, entity T, options ...Option) error {
 124		db := applyOptions(r.DB, options).WithContext(ctx)
 125		if err := db.Create(&entity).Error; err != nil {
 126			return err
 127		}
 128	
 129		storeCloneIfInTransaction(db, entity)
 130	
 131		return nil
 132	}
 133	
 134	func (r *GormRepository[T]) Save(ctx context.Context, entity T, options ...Option) error {
 135		db := applyOptions(r.DB, options).WithContext(ctx)
 136		return db.Save(&entity).Error
 137	}
...
 159	
 160	func (r *GormRepository[T]) UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error {
 161		db := applyOptions(r.DB, options).WithContext(ctx)
 162	
 163		// Check if we're in a transaction context
 164		txInterface, exists := db.Get(txContextKey)
 165		if !exists {
 166			return fmt.Errorf("UpdateById requires a transaction context with a cloned entity - use WithTx option and ensure entity implements Cloneable[T] and Diffable[T] interfaces")
 167		}
 168	
 169		tx, ok := txInterface.(*Tx)
 170		if !ok {
 171			return fmt.Errorf("invalid transaction context")
 172		}
...
 239	
 240	func (r *GormRepository[T]) DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error {
 241		db := applyOptions(r.DB, options).WithContext(ctx)
 242		return db.Delete(new(T), "id = ?", id).Error
 243	}
 244	
 245	func (r *GormRepository[T]) AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error {
 246		return applyOptions(r.DB, options).
 247			WithContext(ctx).
 248			Model(&entity).
 249			Omit(association + ".*"). // https://gorm.io/docs/associations.html#Using-Omit-to-Exclude-Fields-or-Associations
 250			Association(association).
 251			Append(values)
 252	}
...
 274	
 275	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 276	func (r *GormRepository[T]) BeginTransaction() *Tx {
 277		gtx := r.DB.Begin()
 278		return &Tx{
 279			gtx: gtx,
 280			committed: false,
 281			rolledBack: false,
 282			clonedEntities: make(map[string]interface{}),
 283		}
 284	}
 285	
 286	// WithTx returns an option to run the query within a transaction.
 287	// When used with Find operations, it automatically clones entities that support cloning.
 288	func WithTx(tx *Tx) Option {
 289		return func(db *gorm.DB) *gorm.DB {
 290			// Store the transaction reference in the context for later use
 291			return tx.gtx.Set(txContextKey, tx)
 292		}
 293	}
 294	
 295	// WithQuery returns an option to customize the query.
 296	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 297		return func(db *gorm.DB) *gorm.DB {
 298			return fn(db)
 299		}
 300	}
 301	
 302	func WithQueryStruct(query map[string]interface{}) Option {
 303		return func(db *gorm.DB) *gorm.DB {
 304			return db.Where(query)
 305		}
 306	}
 307	
 308	type Tx struct {
 309		gtx *gorm.DB
 310		committed bool
 311		rolledBack bool
 312		// clonedEntities stores cloned entities as snapshots during transaction
 313		// key is a unique identifier for the entity, value is the cloned entity snapshot
 314		clonedEntities map[string]interface{}
 315		mutex sync.RWMutex
 316	}
