 15	func setupIntegrationDB(t *testing.T) *gorm.DB {
 16		db, err := gorm.Open(sqlite.Open(":memory:"), &gorm.Config{
 17			Logger: logger.Default.LogMode(logger.Silent),
 18		})
 19		if err != nil {
 20			t.Fatalf("Failed to connect to integration test database: %v", err)
 21		}
 22	
 23		// Auto-migrate all test models
 24		err = db.AutoMigrate(&tests.TestUser{}, &tests.TestProfile{}, &tests.TestPost{}, &tests.TestTag{}, &tests.TestSimpleEntity{})
 25		if err != nil {
 26			t.Fatalf("Failed to migrate integration test models: %v", err)
 27		}
 28	
 29		return db
 30	}
