 60	func (r *GormRepository[T]) FindMany(ctx context.Context, options ...Option) ([]T, error) {
 61		var entities []T
 62		db := applyOptions(r.DB, options).WithContext(ctx)
 63		if err := db.Find(&entities).Error; err != nil {
 64			return nil, err
 65		}
 66		return entities, nil
 67	}
