 36	// AssertPaginationResult validates pagination result structure
 37	func AssertPaginationResult(t *testing.T, result *repositories.PaginationResult[TestUser], expectedTotal int64, expectedPage int, expectedPageSize int, message string) {
 38		t.Helper()
 39	
 40		if result.Total != expectedTotal {
 41			t.Errorf("%s: Total mismatch - expected %d, got %d", message, expectedTotal, result.Total)
 42		}
 43		if result.CurrentPage != expectedPage {
 44			t.Errorf("%s: CurrentPage mismatch - expected %d, got %d", message, expectedPage, result.CurrentPage)
 45		}
 46		if result.Limit != expectedPageSize {
 47			t.Errorf("%s: Limit mismatch - expected %d, got %d", message, expectedPageSize, result.Limit)
 48		}
 49	
 50		expectedLastPage := int((expectedTotal + int64(expectedPageSize) - 1) / int64(expectedPageSize))
 51		if expectedTotal == 0 {
 52			expectedLastPage = 0
 53		}
 54		if result.LastPage != expectedLastPage {
 55			t.Errorf("%s: LastPage mismatch - expected %d, got %d", message, expectedLastPage, result.LastPage)
 56		}
 57	}
