 268	func TestSuite_TransactionScenarios(t *testing.T) {
 269		db := SetupTestDBWithConfig(t, DefaultTestDBConfig())
 270		defer CleanupTestDB(t, db)
 271	
 272		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 273		profileRepo := &repositories.GormRepository[TestProfile]{DB: db}
 274		ctx := context.Background()
 275	
 276		t.Run("ComplexTransactionSuccess", func(t *testing.T) {
 277			var err error
 278			tx := userRepo.BeginTransaction()
 279			defer tx.Finish(&err)
 280	
 281			// Create user in transaction
 282			user := NewTestUserBuilder().
 283				WithName("Transaction User").
 284				WithEmail("transaction@example.com").
 285				Build()
 286	
 287			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 288			if err != nil {
 289				t.Errorf("Failed to create user in transaction: %v", err)
 290				return
 291			}
 292	
 293			// Create profile in transaction
 294			profile := NewTestProfileBuilder(user.ID).
 295				WithBio("Transaction profile bio").
 296				Build()
 297	
 298			err = profileRepo.Create(ctx, profile, repositories.WithTx(tx))
 299			if err != nil {
 300				t.Errorf("Failed to create profile in transaction: %v", err)
 301				return
 302			}
 303	
 304			// Transaction should commit automatically
 305		})
 306	
 307		// Verify both user and profile were created
 308		users, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 309			return db.Where("email = ?", "transaction@example.com")
 310		}))
 311		if err != nil {
 312			t.Fatalf("Failed to find transaction user: %v", err)
 313		}
 314		if len(users) != 1 {
 315			t.Errorf("Expected 1 transaction user, got %d", len(users))
 316		}
 317	
 318		profiles, err := profileRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 319			return db.Where("user_id = ?", users[0].ID)
 320		}))
 321		if err != nil {
 322			t.Fatalf("Failed to find transaction profile: %v", err)
 323		}
 324		if len(profiles) != 1 {
 325			t.Errorf("Expected 1 transaction profile, got %d", len(profiles))
 326		}
 327	
 328		t.Run("ComplexTransactionFailure", func(t *testing.T) {
 329			var err error
 330			tx := userRepo.BeginTransaction()
 331			defer tx.Finish(&err)
 332	
 333			// Create user in transaction
 334			user := NewTestUserBuilder().
 335				WithName("Failed Transaction User").
 336				WithEmail("failed@example.com").
 337				Build()
 338	
 339			err = userRepo.Create(ctx, user, repositories.WithTx(tx))
 340			if err != nil {
 341				t.Errorf("Failed to create user in transaction: %v", err)
 342				return
 343			}
 344	
 345			// Simulate an error
 346			err = gorm.ErrInvalidTransaction
 347	
 348			// Transaction should rollback automatically
 349		})
 350	
 351		// Verify user was not created due to rollback
 352		failedUsers, err := userRepo.FindMany(ctx, repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 353			return db.Where("email = ?", "failed@example.com")
 354		}))
 355		if err != nil {
 356			t.Fatalf("Failed to search for failed transaction user: %v", err)
 357		}
 358		if len(failedUsers) != 0 {
 359			t.Errorf("Expected 0 failed transaction users, got %d", len(failedUsers))
 360		}
 361	}
