 375	func TestGormRepository_Transaction_Rollback(t *testing.T) {
 376		db := setupTestDB(t)
 377		repo := &GormRepository[tests.TestUser]{DB: db}
 378		ctx := context.Background()
 379	
 380		tx := repo.BeginTransaction()
 381	
 382		user := createTestUser()
 383		err := repo.Create(ctx, user, WithTx(tx))
 384		if err != nil {
 385			t.Errorf("Create in transaction failed: %v", err)
 386		}
 387	
 388		// Rollback the transaction
 389		err = tx.Rollback()
 390		if err != nil {
 391			t.Errorf("Transaction rollback failed: %v", err)
 392		}
 393	
 394		// Verify the user was not created
 395		var count int64
 396		db.Model(&tests.TestUser{}).Count(&count)
 397		if count != 0 {
 398			t.Errorf("Expected 0 users after rollback, got %d", count)
 399		}
 400	}
