 193				Age: 25,
 194				Active: true,
 195			}
 196	
 197			err = repo.Create(ctx, user, WithTx(tx))
 198			if err != nil {
 199				tx.Rollback()
 200				b.Fatalf("Create in transaction failed: %v", err)
 201			}
 202	
 203			err = tx.Commit()
 204			if err != nil {
 205				b.Fatalf("Transaction commit failed: %v", err)
 206			}
 207		}
 208	}
