 222				Email: fmt.Sprintf("benchmark%d@example.com", i),
 223				Age: 25,
 224				Active: true,
 225			}
 226			userIDs[i] = user.ID
 227	
 228			err := userRepo.Create(ctx, user)
 229			if err != nil {
 230				b.Fatalf("Failed to create benchmark user: %v", err)
 231			}
 232	
 233			profile := tests.TestProfile{
 234				ID: uuid.New(),
 235				UserID: user.ID,
 236				Bio: fmt.Sprintf("Benchmark bio %d", i),
 237				Website: fmt.Sprintf("https://benchmark%d.example.com", i),
 238			}
 239			err = profileRepo.Create(ctx, profile)
 240			if err != nil {
 241				b.Fatalf("Failed to create benchmark profile: %v", err)
 242			}
 243		}
 244	
 245		b.ResetTimer()
 246		for i := 0; i < b.N; i++ {
 247			_, err := userRepo.FindById(ctx, userIDs[i%100], WithRelations("Profile"))
 248			if err != nil {
