 1	package repositories
 2	
 3	import (
 4		"context"
 5	
 6		"github.com/google/uuid"
 7		"gorm.io/gorm"
 8	)
 9	
 10	// Option represents a functional option for configuring the repository methods.
 11	type Option func(*gorm.DB) *gorm.DB
 12	
 13	type PaginationResult[T any] struct {
 14		Data []T `json:"data"`
 15		Total int64 `json:"total"`
 16		Limit int `json:"limit"`
 17		Offset int `json:"offset"`
 18		CurrentPage int `json:"currentPage"`
 19		LastPage int `json:"lastPage"`
 20		From int `json:"from"`
 21		To int `json:"to"`
 22	}
 23	
 24	// Interface methods to avoid circular dependency with test helpers
 25	func (p *PaginationResult[T]) GetTotal() int64 {
 26		return p.Total
 27	}
 28	
 29	func (p *PaginationResult[T]) GetCurrentPage() int {
 30		return p.CurrentPage
 31	}
 32	
 33	func (p *PaginationResult[T]) GetLimit() int {
 34		return p.Limit
 35	}
 36	
 37	func (p *PaginationResult[T]) GetLastPage() int {
 38		return p.LastPage
 39	}
 40	
 41	func (p *PaginationResult[T]) GetData() []T {
 42		return p.Data
 43	}
 44	
 45	// Diffable represents entities that can generate clones and diffs
 46	type Diffable[T any] interface {
 47		Clone() T
 48		Diff(T) map[string]interface{}
 49	}
 50	
 51	type Repository[T any] interface {
 52		FindMany(ctx context.Context, options ...Option) ([]T, error)
 53		FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error)
 54		FindById(ctx context.Context, id uuid.UUID, options ...Option) (T, error)
 55		FindOne(ctx context.Context, options ...Option) (T, error)
 56		Create(ctx context.Context, entity T, options ...Option) error
 57		Save(ctx context.Context, entity T, options ...Option) error
 58		UpdateById(ctx context.Context, id uuid.UUID, entity T, options ...Option) error
 59		UpdateByIdWithMask(ctx context.Context, id uuid.UUID, mask map[string]interface{}, entity T, options ...Option) error
 60		UpdateByIdWithMap(ctx context.Context, id uuid.UUID, values map[string]interface{}, options ...Option) (T, error)
 61		UpdateByIdInPlace(ctx context.Context, id uuid.UUID, entity Diffable[T], updateFunc func(Diffable[T]), options ...Option) error
 62		DeleteById(ctx context.Context, id uuid.UUID, options ...Option) error
 63		BeginTransaction() *Tx
 64		AppendAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 65		RemoveAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 66		ReplaceAssociation(ctx context.Context, entity T, association string, values interface{}, options ...Option) error
 67		GetDB() *gorm.DB
 68	}
