 25	// NewGormRepository creates a new instance of GormRepository with the provided GORM database connection.
 26	// T is the entity type that this repository will manage.
 27	func NewGormRepository[T any](db *gorm.DB) *GormRepository[T] {
 28		return &GormRepository[T]{
 29			DB: db,
 30		}
 31	}
 32	
 33	func WithRelations(relations ...string) Option {
 34		return func(db *gorm.DB) *gorm.DB {
 35			for _, relation := range relations {
 36				db = db.Preload(relation)
 37			}
 38			return db
 39		}
 40	}
 41	
 42	func applyOptions(db *gorm.DB, options []Option) *gorm.DB {
 43		for _, option := range options {
 44			if option != nil {
 45				db = option(db)
 46			}
 47		}
 48		return db
 49	}
 50	
 51	func newEntity[T any]() T {
 52		var entity T
 53		entityType := reflect.TypeOf(entity)
 54		if entityType.Kind() == reflect.Ptr {
 55			return reflect.New(entityType.Elem()).Interface().(T)
 56		}
 57		return entity
 58	}
 59	
 60	func (r *GormRepository[T]) FindMany(ctx context.Context, options ...Option) ([]T, error) {
 61		var entities []T
 62		db := applyOptions(r.DB, options).WithContext(ctx)
 63		if err := db.Find(&entities).Error; err != nil {
 64			return nil, err
 65		}
 66		return entities, nil
 67	}
 68	
 69	// FindPaginated retrieves records with pagination.
 70	func (r *GormRepository[T]) FindPaginated(ctx context.Context, page int, pageSize int, options ...Option) (*PaginationResult[T], error) {
 71		var entities []T
 72		var totalRows int64
 73	
 74		db := applyOptions(r.DB, options).WithContext(ctx)
 75		db.Model(&entities).Count(&totalRows)
 76	
 77		offset := (page - 1) * pageSize
 78		if err := db.Offset(offset).Limit(pageSize).Find(&entities).Error; err != nil {
 79			return nil, err
 80		}
 81	
 82		result := &PaginationResult[T]{
 83			Data: entities,
 84			Total: totalRows,
 85			Limit: pageSize,
 86			Offset: offset,
 87			CurrentPage: page,
 88			LastPage: int((totalRows + int64(pageSize) - 1) / int64(pageSize)),
 89			From: offset + 1,
 90			To: offset + len(entities),
 91		}
 92	
 93		return result, nil
 94	}
 95	
 96	func (r *GormRepository[T]) FindOne(ctx context.Context, options ...Option) (T, error) {
 97		var entity T
 98		db := applyOptions(r.DB, options).WithContext(ctx)
 99	
 100		if err := db.First(&entity).Error; err != nil {
 101			return entity, err
 102		}
 103	
 104		// Store clone if in transaction and supports cloning
 105		storeCloneIfInTransaction(db, entity)
 106	
 107		return entity, nil
 108	}
