 139	func (r *GormRepository[T]) UpdateByIdWithMap(ctx context.Context, id uuid.UUID, values map[string]interface{}, options ...Option) (T, error) {
 140		db := applyOptions(r.DB, options).WithContext(ctx)
 141		entity := newEntity[T]()
 142	
 143		if err := db.Model(&entity).Where("id = ?", id).Updates(values).Error; err != nil {
 144			return entity, err
 145		}
 146		return entity, nil
 147	}
