 169	
 170		userRepo := &repositories.GormRepository[TestUser]{DB: db}
 171		postRepo := &repositories.GormRepository[TestPost]{DB: db}
 172		tagRepo := &repositories.GormRepository[TestTag]{DB: db}
 173		ctx := context.Background()
 174	
 175		t.Run("UserWithPostsAndTags", func(t *testing.T) {
 176			user := NewTestUserBuilder().
 177				WithName("Blogger User").
 178				WithEmail("blogger@example.com").
 179				Build()
 180	
 181			err := userRepo.Create(ctx, user)
 182			if err != nil {
 183				t.Fatalf("Failed to create user: %v", err)
 184			}
 185	
 186			// Create tags
 187			tag1 := TestTag{ID: uuid.New(), Name: "Go"}
 188			tag2 := TestTag{ID: uuid.New(), Name: "Testing"}
 189			tag3 := TestTag{ID: uuid.New(), Name: "GORM"}
 190	
 191			for _, tag := range []TestTag{tag1, tag2, tag3} {
 192				err = tagRepo.Create(ctx, tag)
 193				if err != nil {
 194					t.Fatalf("Failed to create tag %s: %v", tag.Name, err)
 195				}
 196			}
 197	
 198			// Create posts
 199			post1 := NewTestPostBuilder(user.ID).
 200				WithTitle("Introduction to Go").
 201				WithContent("Go is a great programming language...").
 202				WithPublished(true).
 203				Build()
 204	
 205			post2 := NewTestPostBuilder(user.ID).
 206				WithTitle("Testing with GORM").
 207				WithContent("GORM makes database testing easier...").
 208				WithPublished(false).
 209				Build()
 210	
 211			err = postRepo.Create(ctx, post1)
 212			if err != nil {
 213				t.Fatalf("Failed to create post1: %v", err)
 214			}
 215			err = postRepo.Create(ctx, post2)
 216			if err != nil {
 217				t.Fatalf("Failed to create post2: %v", err)
 218			}
 219	
 220			// Associate tags with posts
 221			err = postRepo.AppendAssociation(ctx, post1, "Tags", []TestTag{tag1, tag2})
 222			if err != nil {
 223				t.Fatalf("Failed to associate tags with post1: %v", err)
 224			}
 225	
 226			err = postRepo.AppendAssociation(ctx, post2, "Tags", []TestTag{tag2, tag3})
 227			if err != nil {
 228				t.Fatalf("Failed to associate tags with post2: %v", err)
 229			}
 230	
 231			// Find user with posts and their tags
 232			foundUser, err := userRepo.FindById(ctx, user.ID, repositories.WithRelations("Posts", "Posts.Tags"))
 233			if err != nil {
 234				t.Fatalf("Failed to find user with posts and tags: %v", err)
 235			}
 236	
 237			if len(foundUser.Posts) != 2 {
 238				t.Errorf("Expected 2 posts, got %d", len(foundUser.Posts))
 239			}
 240	
 241			// Check that posts have tags
 242			for _, post := range foundUser.Posts {
 243				if len(post.Tags) == 0 {
 244					t.Errorf("Expected post %s to have tags", post.Title)
 245				}
 246			}
 247	
 248			// Find published posts only
 249			publishedPosts, err := postRepo.FindMany(ctx,
 250				repositories.WithQuery(func(db *gorm.DB) *gorm.DB {
 251					return db.Where("user_id = ? AND published = ?", user.ID, true)
 252				}),
 253				repositories.WithRelations("Tags"),
 254			)
 255			if err != nil {
 256				t.Fatalf("Failed to find published posts: %v", err)
 257			}
 258	
 259			if len(publishedPosts) != 1 {
 260				t.Errorf("Expected 1 published post, got %d", len(publishedPosts))
 261			}
 262			if publishedPosts[0].Title != "Introduction to Go" {
 263				t.Errorf("Expected published post title 'Introduction to Go', got %s", publishedPosts[0].Title)
 264			}
 265		})
 266	}
