 15	// setupTestDB creates an in-memory SQLite database for testing
 16	func setupTestDB(t *testing.T) *gorm.DB {
 17		// Use a unique database name for each test to ensure isolation
 18		dbName := ":memory:"
 19		db, err := gorm.Open(sqlite.Open(dbName), &gorm.Config{
 20			Logger: logger.Default.LogMode(logger.Silent),
 21		})
 22		if err != nil {
 23			t.Fatalf("Failed to connect to test database: %v", err)
 24		}
 25	
 26		// Auto-migrate test models
 27		err = db.AutoMigrate(&tests.TestUser{}, &tests.TestProfile{}, &tests.TestPost{}, &tests.TestTag{}, &tests.TestSimpleEntity{})
 28		if err != nil {
 29			t.Fatalf("Failed to migrate test models: %v", err)
 30		}
 31	
 32		return db
 33	}
