 249		user := createTestUser()
 250		profile := tests.TestProfile{
 251			ID: uuid.New(),
 252			UserID: user.ID,
 253			Bio: "Test bio",
 254			Website: "https://example.com",
 255		}
 256	
 257		err := repo.Create(ctx, user)
 258		if err != nil {
 259			t.Fatalf("Failed to create test user: %v", err)
 260		}
 261	
 262		// Create profile separately
 263		err = db.Create(&profile).Error
 264		if err != nil {
 265			t.Fatalf("Failed to create test profile: %v", err)
 266		}
 267	
 268		// Find user with profile preloaded
 269		foundUser, err := repo.FindById(ctx, user.ID, WithRelations("Profile"))
 270		if err != nil {
 271			t.Errorf("FindById with relations failed: %v", err)
 272		}
 273	
 274		if foundUser.Profile == nil {
 275			t.Error("Expected profile to be loaded, but it was nil")
 276		} else if foundUser.Profile.Bio != "Test bio" {
 277			t.Errorf("Expected profile bio 'Test bio', got %s", foundUser.Profile.Bio)
 278		}
 279	}
