 343	func TestGormRepository_Transaction_Commit(t *testing.T) {
 344		db := setupTestDB(t)
 345		repo := &GormRepository[tests.TestUser]{DB: db}
 346		ctx := context.Background()
 347	
 348		tx := repo.BeginTransaction()
 349		defer func() {
 350			if !tx.committed && !tx.rolledBack {
 351				tx.Rollback()
 352			}
 353		}()
 354	
 355		user := createTestUser()
 356		err := repo.Create(ctx, user, WithTx(tx))
 357		if err != nil {
 358			t.Errorf("Create in transaction failed: %v", err)
 359		}
 360	
 361		// Commit the transaction
 362		err = tx.Commit()
 363		if err != nil {
 364			t.Errorf("Transaction commit failed: %v", err)
 365		}
 366	
 367		// Verify the user was created
 368		var count int64
 369		db.Model(&tests.TestUser{}).Count(&count)
 370		if count != 1 {
 371			t.Errorf("Expected 1 user after commit, got %d", count)
 372		}
 373	}
 374	
 375	func TestGormRepository_Transaction_Rollback(t *testing.T) {
 376		db := setupTestDB(t)
 377		repo := &GormRepository[tests.TestUser]{DB: db}
 378		ctx := context.Background()
 379	
 380		tx := repo.BeginTransaction()
 381	
 382		user := createTestUser()
 383		err := repo.Create(ctx, user, WithTx(tx))
 384		if err != nil {
 385			t.Errorf("Create in transaction failed: %v", err)
 386		}
 387	
 388		// Rollback the transaction
 389		err = tx.Rollback()
 390		if err != nil {
 391			t.Errorf("Transaction rollback failed: %v", err)
 392		}
 393	
 394		// Verify the user was not created
 395		var count int64
 396		db.Model(&tests.TestUser{}).Count(&count)
 397		if count != 0 {
 398			t.Errorf("Expected 0 users after rollback, got %d", count)
 399		}
 400	}
