 108	func TestIntegration_TransactionWorkflow(t *testing.T) {
 109		db := setupIntegrationDB(t)
 110		userRepo := &GormRepository[tests.TestUser]{DB: db}
 111		ctx := context.Background()
 112	
 113		// Test successful transaction
 114		t.Run("Successful Transaction", func(t *testing.T) {
 115			var err error
 116			tx := userRepo.BeginTransaction()
 117			defer tx.Finish(&err)
 118	
 119			user1 := tests.TestUser{
 120				ID: uuid.New(),
 121				Name: "Transaction User 1",
 122				Email: "tx1@example.com",
 123				Age: 25,
 124				Active: true,
 125			}
 126	
 127			user2 := tests.TestUser{
 128				ID: uuid.New(),
 129				Name: "Transaction User 2",
 130				Email: "tx2@example.com",
 131				Age: 30,
 132				Active: true,
 133			}
 134	
 135			err = userRepo.Create(ctx, user1, WithTx(tx))
 136			if err != nil {
 137				t.Errorf("Failed to create user1 in transaction: %v", err)
 138				return
 139			}
 140	
 141			err = userRepo.Create(ctx, user2, WithTx(tx))
 142			if err != nil {
 143				t.Errorf("Failed to create user2 in transaction: %v", err)
 144				return
 145			}
 146	
 147			// Transaction should commit automatically via defer
 148		})
 149	
 150		// Verify both users were created
 151		users, err := userRepo.FindMany(ctx, WithQuery(func(db *gorm.DB) *gorm.DB {
 152			return db.Where("email IN ?", []string{"tx1@example.com", "tx2@example.com"})
 153		}))
 154		if err != nil {
 155			t.Fatalf("Failed to find transaction users: %v", err)
 156		}
 157	
 158		if len(users) != 2 {
 159			t.Errorf("Expected 2 users after successful transaction, got %d", len(users))
 160		}
 161	
 162		// Test failed transaction
 163		t.Run("Failed Transaction", func(t *testing.T) {
 164			var err error
 165			tx := userRepo.BeginTransaction()
 166			defer tx.Finish(&err)
 167	
 168			user3 := tests.TestUser{
 169				ID: uuid.New(),
 170				Name: "Transaction User 3",
 171				Email: "tx3@example.com",
 172				Age: 35,
 173				Active: true,
 174			}
 175	
 176			err = userRepo.Create(ctx, user3, WithTx(tx))
 177			if err != nil {
 178				t.Errorf("Failed to create user3 in transaction: %v", err)
 179				return
 180			}
 181	
 182			// Simulate an error
 183			err = gorm.ErrInvalidTransaction
 184	
 185			// Transaction should rollback automatically via defer
 186		})
 187	
 188		// Verify user3 was not created due to rollback
 189		user3Count := int64(0)
 190		db.Model(&tests.TestUser{}).Where("email = ?", "tx3@example.com").Count(&user3Count)
 191		if user3Count != 0 {
 192			t.Errorf("Expected 0 users with email tx3@example.com after rollback, got %d", user3Count)
 193		}
 194	}
