 179	func BenchmarkGormRepository_Transaction(b *testing.B) {
 180		db := setupBenchmarkDB(b)
 181		repo := &GormRepository[tests.TestUser]{DB: db}
 182		ctx := context.Background()
 183	
 184		b.ResetTimer()
 185		for i := 0; i < b.N; i++ {
 186			var err error
 187			tx := repo.BeginTransaction()
 188	
 189			user := tests.TestUser{
 190				ID: uuid.New(),
 191				Name: fmt.Sprintf("Transaction User %d", i),
 192				Email: fmt.Sprintf("tx%d@example.com", i),
 193				Age: 25,
 194				Active: true,
 195			}
 196	
 197			err = repo.Create(ctx, user, WithTx(tx))
