 271	func (r *GormRepository[T]) GetDB() *gorm.DB {
 272		return r.DB
 273	}
 274	
 275	// BeginTransaction starts a new transaction that should be used with defer for automatic cleanup
 276	func (r *GormRepository[T]) BeginTransaction() *Tx {
 277		gtx := r.DB.Begin()
 278		return &Tx{
 279			gtx: gtx,
 280			committed: false,
 281			rolledBack: false,
 282			clonedEntities: make(map[string]interface{}),
 283		}
 284	}
 285	
 286	// WithTx returns an option to run the query within a transaction.
 287	// When used with Find operations, it automatically clones entities that support cloning.
 288	func WithTx(tx *Tx) Option {
 289		return func(db *gorm.DB) *gorm.DB {
 290			// Store the transaction reference in the context for later use
 291			return tx.gtx.Set(txContextKey, tx)
 292		}
 293	}
 294	
 295	// WithQuery returns an option to customize the query.
 296	func WithQuery(fn func(*gorm.DB) *gorm.DB) Option {
 297		return func(db *gorm.DB) *gorm.DB {
 298			return fn(db)
 299		}
 300	}
 301	
 302	func WithQueryStruct(query map[string]interface{}) Option {
 303		return func(db *gorm.DB) *gorm.DB {
 304			return db.Where(query)
 305		}
 306	}
 307	
 308	type Tx struct {
 309		gtx *gorm.DB
 310		committed bool
 311		rolledBack bool
 312		// clonedEntities stores cloned entities as snapshots during transaction
 313		// key is a unique identifier for the entity, value is the cloned entity snapshot
 314		clonedEntities map[string]interface{}
 315		mutex sync.RWMutex
 316	}
 317	
 318	// BeginTransaction starts a nested transaction
 319	func (tx *Tx) BeginTransaction() *Tx {
 320		gtx := tx.gtx.Begin()
 321		return &Tx{
 322			gtx: gtx,
 323			committed: false,
 324			rolledBack: false,
 325			clonedEntities: make(map[string]interface{}),
 326		}
 327	}
 328	
 329	// Commit commits the transaction
 330	func (tx *Tx) Commit() error {
 331		if tx.committed || tx.rolledBack {
 332			return nil
 333		}
 334	
 335		err := tx.gtx.Commit().Error
 336		if err == nil {
 337			tx.committed = true
 338		}
 339		return err
 340	}
 341	
 342	// Rollback rolls back the transaction
 343	func (tx *Tx) Rollback() error {
 344		if tx.committed || tx.rolledBack {
 345			return nil
 346		}
 347	
 348		err := tx.gtx.Rollback().Error
 349		if err == nil {
 350			tx.rolledBack = true
 351		}
 352		return err
 353	}
 354	
 355	// Finish should be called with defer to automatically handle commit/rollback
 356	// Usage: defer tx.Finish(&err)
 357	// Use this for simple cases where you don't need complex error handling
 358	// Will commit if err is nil, rollback if err is set
 359	func (tx *Tx) Finish(err *error) {
 360		if tx.committed || tx.rolledBack {
 361			return
 362		}
 363	
 364		if *err != nil {
 365			// If there was an error, rollback
 366			if rollbackErr := tx.Rollback(); rollbackErr != nil {
 367				// Log rollback error but don't override the original error
 368				// You might want to use your logging framework here
 369			}
 370		} else {
 371			// If no error, commit
 372			if commitErr := tx.Commit(); commitErr != nil {
 373				*err = commitErr
 374			}
 375		}
 376	}
 377	
 378	// Error returns any error from the underlying GORM transaction
 379	func (tx *Tx) Error() error {
 380		return tx.gtx.Error
 381	}
 382	
 383	// storeClonedEntity stores the original entity before cloning
 384	func (tx *Tx) storeClonedEntity(entityKey string, original interface{}) {
 385		tx.mutex.Lock()
 386		defer tx.mutex.Unlock()
 387		tx.clonedEntities[entityKey] = original
 388	}
 389	
 390	// getClonedEntity retrieves the original entity if it was cloned
 391	func (tx *Tx) getClonedEntity(entityKey string) (interface{}, bool) {
 392		tx.mutex.RLock()
 393		defer tx.mutex.RUnlock()
 394		original, exists := tx.clonedEntities[entityKey]
 395		return original, exists
 396	}
 397	
 398	// generateEntityKey creates a unique key for an entity based on its type and ID
 399	func generateEntityKey(entity interface{}) string {
 400		entityType := reflect.TypeOf(entity)
 401		if entityType.Kind() == reflect.Ptr {
 402			entityType = entityType.Elem()
 403		}
 404	
 405		// Try to get ID field using reflection
 406		entityValue := reflect.ValueOf(entity)
 407		if entityValue.Kind() == reflect.Ptr {
 408			entityValue = entityValue.Elem()
 409		}
 410	
 411		idField := entityValue.FieldByName("Id")
 412		if !idField.IsValid() {
 413			// Fallback to memory address if no ID field
 414			return fmt.Sprintf("%s_%p", entityType.Name(), entity)
 415		}
 416	
 417		return fmt.Sprintf("%s_%v", entityType.Name(), idField.Interface())
 418	}
 419	
 420	// storeCloneIfInTransaction stores a clone of the entity if we're in a transaction and the entity supports cloning
 421	func storeCloneIfInTransaction[T any](db *gorm.DB, entity T) {
 422		// Check if we're in a transaction context
 423		txInterface, exists := db.Get(txContextKey)
 424		if !exists {
 425			return
 426		}
 427	
 428		tx, ok := txInterface.(*Tx)
 429		if !ok {
 430			return
 431		}
 432	
 433		// Check if entity supports cloning
 434		cloneable, ok := any(entity).(Diffable[T])
 435		if !ok {
 436			return
 437		}
 438	
 439		// Store the cloned entity as a snapshot
 440		entityKey := generateEntityKey(entity)
 441		clone := cloneable.Clone()
 442		tx.storeClonedEntity(entityKey, clone)
 443	}
