 9	// TestUser represents a test entity for repository testing
 10	type TestUser struct {
 11		ID uuid.UUID `gorm:"type:text;primary_key" json:"id"`
 12		Name string `gorm:"not null" json:"name"`
 13		Email string `gorm:"unique;not null" json:"email"`
 14		Age int `json:"age"`
 15		Active bool `json:"active"`
 16		Profile *TestProfile `gorm:"foreignKey:UserID" json:"profile,omitempty"`
 17		Posts []TestPost `gorm:"foreignKey:UserID" json:"posts,omitempty"`
 18		CreatedAt time.Time `json:"createdAt"`
 19		UpdatedAt time.Time `json:"updatedAt"`
 20	}
 21	
 22	// Clone implements the Cloneable interface
 23	func (u TestUser) Clone() TestUser {
 24		clone := u
 25		if u.Profile != nil {
 26			profileClone := *u.Profile
 27			clone.Profile = &profileClone
 28		}
 29		if u.Posts != nil {
 30			clone.Posts = make([]TestPost, len(u.Posts))
 31			copy(clone.Posts, u.Posts)
 32		}
 33		return clone
 34	}
 35	
 36	// Diff implements the Diffable interface
 37	func (u TestUser) Diff(other TestUser) map[string]interface{} {
 38		diff := make(map[string]interface{})
 39	
 40		if u.Name != other.Name {
 41			diff["name"] = u.Name
 42		}
 43		if u.Email != other.Email {
 44			diff["email"] = u.Email
 45		}
 46		if u.Age != other.Age {
 47			diff["age"] = u.Age
 48		}
 49		if u.Active != other.Active {
 50			diff["active"] = u.Active
 51		}
 52	
 53		return diff
 54	}
